import React, {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import { Button } from './Button';

function Navbar() {
  const [click, setClick] = useState(false);
  const [home, setHome] = useState(true);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenuHome = () => {
    setClick(false);
    setHome(true);
  }

  const closeMobileMenuAbout = () => {
    setClick(false);
    setHome(false);
  }


  const showButton = () => {
    if(window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  /*only render "about me" once*/
  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

    return (
        <>
          <nav className="navbar">
              <div className="navbar-container">
                <Link to="/" 
                  className="navbar-logo"
                  id="icon"
                  onClick={closeMobileMenuHome}
                >
                  RL
                </Link>
                <div className='menu-icon' onClick={handleClick}>
                  <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                </div>
                <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                  <li className='nav-item'>
                    <Link to='/' 
                      className={ home ? 'nav-links curr' : 'nav-links'}
                      id='home'
                      onClick={closeMobileMenuHome}
                    >
                      Home
                    </Link>
                  </li>

                  <li className='nav-item'>
                    <Link to='/about-me' 
                      className={ home ? 'nav-links' : 'nav-links curr'}

                      onClick={closeMobileMenuAbout}
                    >
                      About
                    </Link>
                  </li>

                </ul>
              </div>
          </nav>
        </>
    )
}

export default Navbar
